import React, { useEffect, useState } from "react";
import { projectFireStore } from "../firebase/config";
import { collection, getDocs } from "firebase/firestore";

const UseFirestore = (collectionImag) => {
  const [ImagesList, setImages] = useState([]);
  let images = collection(projectFireStore, collectionImag);
  async function getDocsfunc() {
    let snapshot = await getDocs(images);
    return snapshot;
  }
  useEffect(() => {
    async function getData() {
      let data = await getDocsfunc();
      let imageArr = [];
      data.forEach((element) => {
        imageArr.push({ ...element.data(), id: element.id });
      });
      setImages(imageArr);
    }
    getData();
  }, [collectionImag]);
  console.log(ImagesList);
  return ImagesList;
};

export default UseFirestore;
